  const [isSignUp, setIsSignUp] = useState(location.pathname === "/register");
  const navigate = useNavigate();

  useEffect(() => {
    setIsSignUp(location.pathname === "/register");
  }, [location.pathname]);

  const handleToggle = (showSignUp) => {
    setIsSignUp(showSignUp);
    navigate(showSignUp ? "/register" : "/login");
  };

  const [formData, setFormData] = useState({
    name: "",
    email: "",
    dob: "",
    phone: "",
    password: "",
    confirmPassword: "",
    role: "patient"
  });

  const [error, setError] = useState("");

  const sampleUsers = [
    { email: "admin@medilocker.com", password: "admin123", role: "admin" },
    { email: "kumaran@medilocker.com", password: "kumaran123", role: "patient" },
    { email: "john@medilocker.com", password: "john123", role: "doctor" }
  ];

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const validateForm = () => {
    if (isSignUp) {
      if (!formData.name || !formData.email || !formData.dob || !formData.phone || !formData.password || !formData.confirmPassword) {
        Swal.fire("Error", "All fields are required!", "error");
        return false;
      }
      if (formData.password !== formData.confirmPassword) {
        Swal.fire("Error", "Passwords do not match!", "error");
        return false;
      }
    } else {
      if (!formData.email || !formData.password) {
        Swal.fire("Error", "Email and Password are required!", "error");
        return false;
      }
    }
    return true;
  };

  const handleLogin = (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    const user = sampleUsers.find(user => user.email === formData.email && user.password === formData.password && user.role === formData.role);
    if (user) {
      localStorage.setItem("isLoggedIn", "true");
      localStorage.setItem("userRole", user.role);
      localStorage.setItem("userEmail", user.email);
      Swal.fire("Success", "Login Successful!", "success");
      navigate(user.role === "admin" ? "/admindashboard" : user.role === "patient" ? "/dashboard" : "/doctordashboard");
    } else {
      Swal.fire("Error", "Invalid email, password, or role selection.", "error");
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!validateForm()) return;

    Swal.fire("Success", "Registration Successful!", "success");
    console.log("Registered User:", formData);
    navigate("/login");
  };
      
return (
    <div className="loginregister">
        <section className="loginregistersection">
            <div className={`wrapper ${isSignUp ? "animated-signup" : "animated-signin"}`}>
                <div className="registerform card sign-up">
                    <div className="card-body">
                        <h2 className="card-title p-3">Create an Account</h2>
                        <form>
                            <div className="form-floating mb-3">
                                <input type="text"
                                    id="floatingName"
                                    name="name"
                                    value={name}
                                    onChange={(e) => setName(e.target.value)}
                                    placeholder="Enter your Name"
                                    required
                                    autoComplete="off"
                                    className="form-control" />
                                <label htmlFor="floatingName">Name</label>
                            </div>
                            <div className="form-floating mb-3">
                                <input type="email"
                                        id="floatingEmail"
                                        name="email"
                                        value={email}
                                        onChange={(e) => setEmail(e.target.value)}
                                        placeholder="Enter your Email"
                                        required
                                        autoComplete="off"
                                        className="form-control" />
                                <label htmlFor="floatingEmail">Email</label>
                            </div>
                            <div className="form-floating mb-3">
                                <input type="date"
                                    id="floatingDate"
                                    name="dob"
                                    value={dob}
                                    onChange={(e) => setDob(e.target.value)}
                                    required
                                    autoComplete="off"
                                    className="form-control" />
                                <label htmlFor="floatingDate">Date of Birth</label>
                            </div>
                            <div className="form-floating mb-3">
                                <input type="tel"
                                    id="floatingPhone"
                                    name="phone"
                                    value={phone}
                                    onChange={(e) => setPhone(e.target.value)}
                                    placeholder="Enter your Phone"
                                    required
                                    autoComplete="off"
                                    className="form-control" />
                                <label htmlFor="floatingPhone">Phone</label>
                            </div>
                            <div className="form-floating mb-3">
                                <input type="password"
                                    id="floatingPassword"
                                    name="password"
                                    value={password}
                                    onChange={(e) => setPassword(e.target.value)}
                                    placeholder="Enter your Password"
                                    required
                                    autoComplete="off"
                                    className="form-control" />
                                <label htmlFor="floatingPassword">Password</label>
                            </div>
                            <div className="form-floating mb-3">
                                <input type="password"
                                    id="floatingConfirmPassword"
                                    name="confirmPassword"
                                    value={confirmPassword}
                                    onChange={(e) => setConfirmPassword(e.target.value)}
                                    placeholder="Confirm your Password"
                                    required
                                    autoComplete="off"
                                    className="form-control" />
                                <label htmlFor="floatingConfirmPassword">Confirm Password</label>
                            </div>
                            <div className="mb-3 p-2">
                                <label className="form-label d-block fw-medium text-start">Select Role:</label>
                                <div className="d-flex align-items-center">
                                    <input type="radio" 
                                        id="doctor" 
                                        name="role" 
                                        value="doctor"
                                        checked={role === "doctor"} 
                                        onChange={(e) => setRole(e.target.value)}
                                        className="me-1" />
                                    <label htmlFor="doctor" className="me-3">Doctor</label>

                                    <input type="radio" 
                                        id="patient" 
                                        name="role" 
                                        value="patient"
                                        checked={role === "patient"} 
                                        onChange={(e) => setRole(e.target.value)}
                                        className="me-1" />
                                    <label htmlFor="patient">Patient</label>
                                </div>
                            </div>
                            <div className="col-12 mt-4">
                                <button type="submit" className="btn btn-success w-100" onClick={handleSubmit}>Register</button>
                            </div>
                            <br />
                            <p className="form-text">
                                Already have an account? <a onClick={() => handleToggle(false)} className="signin-link link">Log in</a>                            
                            </p>
                        </form>
                    </div>
                </div>
                <div className="loginform card sign-in">
                    <div className="card-body mt-3">
                        <h2 className="card-title mt-5 p-3">Welcome Back!</h2>
                        <form onSubmit={handleLogin}>
                            <div className="form-floating mb-3">
                                <input type="email"
                                    id="floatingInput"
                                    name="email"
                                    value={email}
                                    onChange={(e) => setEmail(e.target.value)}
                                    placeholder="Enter your email"
                                    required
                                    autoComplete="off"
                                    className="form-control" />
                                <label htmlFor="floatingInput">Email</label>
                            </div>
                            <div className="form-floating mb-3">
                                <input type="password"
                                    id="floatingPassword"
                                    name="password"
                                    value={password}
                                    onChange={(e) => setPassword(e.target.value)}
                                    placeholder="Enter your Password"
                                    required
                                    autoComplete="off"
                                    className="form-control" />
                                <label htmlFor="floatingPassword">Password</label>
                            </div>
                            <div className="mb-3 p-2">
                                <label className="form-label d-block fw-medium text-start">Select Role:</label>
                                <div className="d-flex align-items-center">
                                    <input type="radio"
                                        id="admin"
                                        name="role"
                                        value="admin"
                                        checked={role === "admin"}
                                        onChange={(e) => setRole(e.target.value)}
                                        className="me-1" />
                                    <label htmlFor="admin" className="me-3">Admin</label>

                                    <input type="radio"
                                        id="patient"
                                        name="role"
                                        value="patient"
                                        checked={role === "patient"}
                                        onChange={(e) => setRole(e.target.value)}
                                        className="me-1" />
                                    <label htmlFor="patient" className="me-3">Patient</label>

                                    <input type="radio"
                                        id="doctor"
                                        name="role"
                                        value="doctor"
                                        checked={role === "doctor"}
                                        onChange={(e) => setRole(e.target.value)}
                                        className="me-1" />
                                    <label htmlFor="doctor">Doctor</label>
                                </div>
                            </div>

                            {error && <p className="text-danger">{error}</p>}

                            <div className="col-12 mt-4">
                                <button type="submit" className="btn btn-success w-100">Login</button>
                            </div>
                            <br />
                            <p className="form-text">
                                <a href="/forgotpassword" className="form-link forget">Forgot Password?</a>
                            </p>
                            <p className="form-text">
                                Don't have an account? <a onClick={() => handleToggle(true)} className="signup-link form-link link">Sign up</a>
                            </p>
                        </form>
                    </div>
                </div>
            </div>
        </section>
    </div>
  );
};

export default LoginRegister;
