import React, { useState } from "react";

const ManagePatient = () => {
  const [patients, setPatients] = useState([
    { id: 1, name: "Alice Smith", dob: "1992-08-21", email: "alice.smith@medilocker.com", phone: "987-654-1234", gender: "Female", address: "123 Main St, Chennai, TN", condition: "Diabetes", treatment: "Insulin Therapy" }
  ]);

  const [formData, setFormData] = useState({
    id: null, name: "", dob: "", email: "", phone: "", gender: "", address: "", condition: "", treatment: ""
  });

  const [isEditing, setIsEditing] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleAddPatient = () => {
    if (!formData.name || !formData.email || !formData.condition || !formData.treatment) {
      alert("Please fill in all required fields.");
      return;
    }
    const newPatient = { ...formData, id: patients.length + 1 };
    setPatients([...patients, newPatient]);
    resetForm();
  };

  const handleEditPatient = (patient) => {
    setFormData(patient);
    setIsEditing(true);
  };

  const handleUpdatePatient = () => {
    setPatients(patients.map(pat => (pat.id === formData.id ? formData : pat)));
    resetForm();
  };

  const handleDeletePatient = (id) => {
    setPatients(patients.filter(pat => pat.id !== id));
  };

  const resetForm = () => {
    setFormData({ id: null, name: "", dob: "", email: "", phone: "", gender: "", address: "", condition: "", treatment: "" });
    setIsEditing(false);
  };

  return (
    <div className="manage-patients">
      <section className="dashboardsection">
        <div className="container mt-4">
          <h2 className="text-start">Manage Patients</h2>
          
          <div className="mt-4">
            <h4>{isEditing ? "Edit Patient" : "Add Patient"}</h4>
            <hr className="border border-3 border-primary" />
            <div className="row g-3 mt-2">
  <div className="col-md-6">
    <div className="form-floating">
      <input 
        type="text"
        id="floatingName"
        name="name"
        value={name}
        onChange={(e) => setName(e.target.value)}
        placeholder="Enter Name"
        required
        autoComplete="off"
        className="form-control"
      />
      <label htmlFor="floatingName">Name</label>
    </div>
  </div>

  <div className="col-md-6">
    <div className="form-floating">
      <input 
        type="date"
        id="floatingDob"
        name="dob"
        value={dob}
        onChange={(e) => setDob(e.target.value)}
        required
        className="form-control"
      />
      <label htmlFor="floatingDob">Date of Birth</label>
    </div>
  </div>

  <div className="col-md-6">
    <div className="form-floating">
      <input 
        type="email"
        id="floatingEmail"
        name="email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        placeholder="Enter Email"
        required
        autoComplete="off"
        className="form-control"
      />
      <label htmlFor="floatingEmail">Email</label>
    </div>
  </div>

  <div className="col-md-6">
    <div className="form-floating">
      <input 
        type="text"
        id="floatingPhone"
        name="phone"
        value={phone}
        onChange={(e) => setPhone(e.target.value)}
        placeholder="Enter Phone Number"
        required
        autoComplete="off"
        className="form-control"
      />
      <label htmlFor="floatingPhone">Phone</label>
    </div>
  </div>

  <div className="col-md-6">
    <div className="form-floating">
      <input 
        type="text"
        id="floatingGender"
        name="gender"
        value={gender}
        onChange={(e) => setGender(e.target.value)}
        placeholder="Enter Gender"
        required
        autoComplete="off"
        className="form-control"
      />
      <label htmlFor="floatingGender">Gender</label>
    </div>
  </div>

  <div className="col-md-6">
    <div className="form-floating">
      <input 
        type="text"
        id="floatingAddress"
        name="address"
        value={address}
        onChange={(e) => setAddress(e.target.value)}
        placeholder="Enter Address"
        required
        autoComplete="off"
        className="form-control"
      />
      <label htmlFor="floatingAddress">Address</label>
    </div>
  </div>

  <div className="col-md-6">
    <div className="form-floating">
      <input 
        type="text"
        id="floatingHeight"
        name="height"
        value={height}
        onChange={(e) => setHeight(e.target.value)}
        placeholder="Enter Height"
        required
        autoComplete="off"
        className="form-control"
      />
      <label htmlFor="floatingHeight">Height</label>
    </div>
  </div>

  <div className="col-md-6">
    <div className="form-floating">
      <input 
        type="text"
        id="floatingWeight"
        name="weight"
        value={weight}
        onChange={(e) => setWeight(e.target.value)}
        placeholder="Enter Weight"
        required
        autoComplete="off"
        className="form-control"
      />
      <label htmlFor="floatingWeight">Weight</label>
    </div>
  </div>

  <div className="col-md-6">
    <div className="form-floating">
      <input 
        type="text"
        id="floatingBloodGroup"
        name="bloodGroup"
        value={bloodGroup}
        onChange={(e) => setBloodGroup(e.target.value)}
        placeholder="Enter Blood Group"
        required
        autoComplete="off"
        className="form-control"
      />
      <label htmlFor="floatingBloodGroup">Blood Group</label>
    </div>
  </div>

  <div className="col-md-6">
    <div className="form-floating">
      <input 
        type="text"
        id="floatingBloodPressure"
        name="bloodPressure"
        value={bloodPressure}
        onChange={(e) => setBloodPressure(e.target.value)}
        placeholder="Enter Blood Pressure"
        required
        autoComplete="off"
        className="form-control"
      />
      <label htmlFor="floatingBloodPressure">Blood Pressure</label>
    </div>
  </div>

  <div className="col-md-6">
    <div className="form-floating">
      <input 
        type="text"
        id="floatingSugarLevel"
        name="sugarLevel"
        value={sugarLevel}
        onChange={(e) => setSugarLevel(e.target.value)}
        placeholder="Enter Sugar Level"
        required
        autoComplete="off"
        className="form-control"
      />
      <label htmlFor="floatingSugarLevel">Sugar Level</label>
    </div>
  </div>
</div>

            <div className="mt-3">
              {isEditing ? (
                <>
                  <button className="btn btn-primary me-2" onClick={handleUpdatePatient}>Update</button>
                  <button className="btn btn-danger" onClick={resetForm}>Cancel</button>
                </>
              ) : (
                <button className="btn btn-primary" onClick={handleAddPatient}>Add Patient</button>
              )}
            </div>
          </div>
          <div className="mt-5">
            <h4>Patients List</h4>
            <hr className="border border-3 border-primary" />
            {patients.length === 0 ? (
              <p className="text-muted">No patients found.</p>
            ) : (
              <ul className="list-group">
                {patients.map((patient) => (
                  <li key={patient.id} className="list-group-item d-flex justify-content-between align-items-center">
                    <div>
                      {patient.name} - {patient.condition} - {patient.phone}
                    </div>
                    <div>
                      <button className="btn btn-warning btn-sm me-2" onClick={() => handleEditPatient(patient)}>Edit</button>
                      <button className="btn btn-danger btn-sm" onClick={() => handleDeletePatient(patient.id)}>Delete</button>
                    </div>
                  </li>
                ))}
              </ul>
            )}
          </div>
        </div>
      </section>
    </div>
  );
};

export default ManagePatient;
