import React, { useState, useEffect } from "react";
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from "recharts";
import Header from "../components/Header";
import Footer from "../components/Footer";

const Dashboard = ({ userName = "Kumaran" }) => {
  const [selectedDoctor, setSelectedDoctor] = useState("");
  const [appointmentDate, setAppointmentDate] = useState("");
  const [appointmentTime, setAppointmentTime] = useState("");
  const [appointments, setAppointments] = useState([]);
  const [sugarData, setSugarData] = useState([]);
  const [beforeEating, setBeforeEating] = useState("");
  const [afterEating, setAfterEating] = useState("");
  const [timeRange, setTimeRange] = useState("week");

  const doctors = [
    { name: "Dr. John - Cardiologist", unavailableTimes: ["10:00", "15:00"] },
    { name: "Dr. Smith - Dermatologist", unavailableTimes: ["12:30", "16:00"] },
    { name: "Dr. Sam - Neurologist", unavailableTimes: ["09:00", "14:00"] },
    { name: "Dr. Johnson - Orthopedic", unavailableTimes: ["11:00", "17:30"] },
  ];

  const handleChange = (e) => setSelectedDoctor(e.target.value);

  const handleBookAppointment = () => {
    if (selectedDoctor && appointmentDate && appointmentTime) {
      const newAppointment = { doctor: selectedDoctor, date: appointmentDate, time: appointmentTime };
      setAppointments([...appointments, newAppointment]);
      setSelectedDoctor("");
      setAppointmentDate("");
      setAppointmentTime("");
    }
  };

  const handleCancelAppointment = (index) => {
    setAppointments(appointments.filter((_, i) => i !== index));
  };

  const isUploadAllowed = () => {
    const now = new Date();
    const hours = now.getHours();
    return hours >= 17 && hours < 19;
  };

  const handleSugarUpload = () => {
    if (beforeEating && afterEating) {
      const today = new Date().toISOString().split("T")[0];
      setSugarData([...sugarData, { date: today, beforeEating, afterEating }]);
      setBeforeEating("");
      setAfterEating("");
    }
  };

  const getFilteredData = () => {
    const now = new Date();
    return sugarData.filter(({ date }) => {
      const dataDate = new Date(date);
      const diffDays = (now - dataDate) / (1000 * 60 * 60 * 24);
      return timeRange === "week" ? diffDays <= 7 : diffDays <= 30;
    });
  };

  return (
    <div className="dashboard">
      <Header />
      <section className="dashboardsection">
        <div className="container mt-4">
          <h2>Dashboard</h2>
          <h3>Welcome, {userName}!</h3>

          <h4 className="mt-4">Sugar Level Tracking</h4>
          <div className="row g-3">
            <div className="col-md-4">
              <input type="number" className="form-control" placeholder="Before Eating" value={beforeEating} onChange={(e) => setBeforeEating(e.target.value)} />
            </div>
            <div className="col-md-4">
              <input type="number" className="form-control" placeholder="After Eating" value={afterEating} onChange={(e) => setAfterEating(e.target.value)} />
            </div>
            <div className="col-md-4">
              <button className="btn btn-primary" onClick={handleSugarUpload} disabled={!isUploadAllowed()}>
                Upload
              </button>
            </div>
          </div>

          <div className="mt-4">
            <h4>Sugar Level Graph</h4>
            <select className="form-select" onChange={(e) => setTimeRange(e.target.value)}>
              <option value="week">Last Week</option>
              <option value="month">Last Month</option>
            </select>
            <ResponsiveContainer width="100%" height={300}>
              <LineChart data={getFilteredData()}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="date" />
                <YAxis />
                <Tooltip />
                <Legend />
                <Line type="monotone" dataKey="beforeEating" stroke="#8884d8" name="Before Eating" />
                <Line type="monotone" dataKey="afterEating" stroke="#82ca9d" name="After Eating" />
              </LineChart>
            </ResponsiveContainer>
          </div>
        </div>
      </section>
      <Footer />
    </div>
  );
};

export default Dashboard;
