import React, { useState } from "react";

const ManagePatient = () => {
  const [patients, setPatients] = useState([
    { id: 1, name: "Alice Smith", dob: "1992-08-21", email: "alice.smith@medilocker.com", phone: "987-654-1234", gender: "Female", address: "123 Main St, Chennai, TN", condition: "Diabetes", treatment: "Insulin Therapy" }
  ]);

  const [formData, setFormData] = useState({
    id: null, name: "", dob: "", email: "", phone: "", gender: "", address: "", condition: "", treatment: ""
  });

  const [isEditing, setIsEditing] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleAddPatient = () => {
    if (!formData.name || !formData.email || !formData.condition || !formData.treatment) {
      alert("Please fill in all required fields.");
      return;
    }
    const newPatient = { ...formData, id: patients.length + 1 };
    setPatients([...patients, newPatient]);
    resetForm();
  };

  const handleEditPatient = (patient) => {
    setFormData(patient);
    setIsEditing(true);
  };

  const handleUpdatePatient = () => {
    setPatients(patients.map(pat => (pat.id === formData.id ? formData : pat)));
    resetForm();
  };

  const handleDeletePatient = (id) => {
    setPatients(patients.filter(pat => pat.id !== id));
  };

  const resetForm = () => {
    setFormData({ id: null, name: "", dob: "", email: "", phone: "", gender: "", address: "", condition: "", treatment: "" });
    setIsEditing(false);
  };

  return (
    <div className="manage-patients">
      <section className="dashboardsection">
        <div className="container mt-4">
          <h2 className="text-start">Manage Patients</h2>
          
          <div className="mt-4">
            <h4>{isEditing ? "Edit Patient" : "Add Patient"}</h4>
            <hr className="border border-3 border-primary" />
            <div className="row g-3 mt-2">
              <div className="col-md-6">
                <div className="form-floating">
                  <input type="text" name="name" value={formData.name} onChange={handleChange} placeholder="Enter Patient Name" required className="form-control" />
                  <label>Name</label>
                </div>
              </div>
              <div className="col-md-6">
                <div className="form-floating">
                  <input type="date" name="dob" value={formData.dob} onChange={handleChange} required className="form-control" />
                  <label>Date of Birth</label>
                </div>
              </div>
              <div className="col-md-6">
                <div className="form-floating">
                  <input type="email" name="email" value={formData.email} onChange={handleChange} placeholder="Enter Email" required className="form-control" />
                  <label>Email</label>
                </div>
              </div>
              <div className="col-md-6">
                <div className="form-floating">
                  <input type="text" name="phone" value={formData.phone} onChange={handleChange} placeholder="Enter Phone Number" required className="form-control" />
                  <label>Phone</label>
                </div>
              </div>
              <div className="col-md-6">
                <div className="form-floating">
                  <input type="text" name="gender" value={formData.gender} onChange={handleChange} placeholder="Enter Gender" required className="form-control" />
                  <label>Gender</label>
                </div>
              </div>
              <div className="col-md-6">
                <div className="form-floating">
                  <input type="text" name="address" value={formData.address} onChange={handleChange} placeholder="Enter Address" required className="form-control" />
                  <label>Address</label>
                </div>
              </div>
              <div className="col-md-6">
                <div className="form-floating">
                  <input type="text" name="condition" value={formData.condition} onChange={handleChange} placeholder="Enter Condition" required className="form-control" />
                  <label>Condition</label>
                </div>
              </div>
              <div className="col-md-6">
                <div className="form-floating">
                  <input type="text" name="treatment" value={formData.treatment} onChange={handleChange} placeholder="Enter Treatment" required className="form-control" />
                  <label>Treatment</label>
                </div>
              </div>
            </div>
            <div className="mt-3">
              {isEditing ? (
                <>
                  <button className="btn btn-primary me-2" onClick={handleUpdatePatient}>Update</button>
                  <button className="btn btn-danger" onClick={resetForm}>Cancel</button>
                </>
              ) : (
                <button className="btn btn-primary" onClick={handleAddPatient}>Add Patient</button>
              )}
            </div>
          </div>
          <div className="mt-5">
            <h4>Patients List</h4>
            <hr className="border border-3 border-primary" />
            {patients.length === 0 ? (
              <p className="text-muted">No patients found.</p>
            ) : (
              <ul className="list-group">
                {patients.map((patient) => (
                  <li key={patient.id} className="list-group-item d-flex justify-content-between align-items-center">
                    <div>
                      {patient.name} - {patient.condition} - {patient.phone}
                    </div>
                    <div>
                      <button className="btn btn-warning btn-sm me-2" onClick={() => handleEditPatient(patient)}>Edit</button>
                      <button className="btn btn-danger btn-sm" onClick={() => handleDeletePatient(patient.id)}>Delete</button>
                    </div>
                  </li>
                ))}
              </ul>
            )}
          </div>
        </div>
      </section>
    </div>
  );
};

export default ManagePatient;
