import React, { useState } from "react";

const AdminDashboard = ({ adminName = "Admin" }) => {
  // Sample data for doctors' leave details
  const [doctorLeaves, setDoctorLeaves] = useState([
    { doctor: "Dr. John - Cardiologist", date: "2025-03-10", reason: "Medical Conference" },
    { doctor: "Dr. Smith - Dermatologist", date: "2025-03-15", reason: "Personal Leave" },
  ]);

  // Sample data for patients' appointments
  const [patientAppointments, setPatientAppointments] = useState([
    { patient: "Kumaran", doctor: "Dr. Sam - Neurologist", date: "2025-03-08", time: "10:30 AM" },
    { patient: "Arjun", doctor: "Dr. Johnson - Orthopedic", date: "2025-03-09", time: "03:00 PM" },
  ]);

  return (
    <div className="admin-dashboard">
      <section className="dashboardsection">
        <div className="container mt-4">
          <h2 className="text-start">Admin Dashboard</h2>
          <h3 className="text-start mt-3">Welcome, {adminName}!</h3>

          {/* Doctors' Leave Details Section */}
          <div className="mt-4">
            <h4>Doctors' Leave Details</h4>
            <hr className="border border-3 border-danger" />
            {doctorLeaves.length === 0 ? (
              <p className="text-muted">No leave requests available.</p>
            ) : (
              <ul className="list-group">
                {doctorLeaves.map((leave, index) => (
                  <li key={index} className="list-group-item">
                    ü©∫ {leave.doctor} - üìÖ {leave.date} - üìù {leave.reason}
                  </li>
                ))}
              </ul>
            )}
          </div>

          {/* Patients' Appointments Section */}
          <div className="mt-5">
            <h4>Patients' Appointments</h4>
            <hr className="border border-3 border-primary" />
            {patientAppointments.length === 0 ? (
              <p className="text-muted">No active appointments.</p>
            ) : (
              <ul className="list-group">
                {patientAppointments.map((appointment, index) => (
                  <li key={index} className="list-group-item">
                    üè• {appointment.patient} - üìÖ {appointment.date} - üïí {appointment.time} - {appointment.doctor}
                  </li>
                ))}
              </ul>
            )}
          </div>
        </div>
      </section>
    </div>
  );
};

export default AdminDashboard;
