import React, { useState,useEffect } from "react";
import { useNavigate,useLocation } from "react-router-dom";
import "../index.css"; 
import Swal from "sweetalert2";

const LoginRegister = () => {
const location = useLocation(); 
const [isSignUp, setIsSignUp] = useState(location.pathname === "/register");

useEffect(() => {
  setIsSignUp(location.pathname === "/register");
}, [location.pathname]); 

const handleToggle = (showSignUp) => {
    setIsSignUp(showSignUp);
    navigate(showSignUp ? "/register" : "/login"); 
};

const [signUpName, setSignUpName] = useState('');
const [signUpEmail, setSignUpEmail] = useState('');
const [signUpDob, setSignUpDob] = useState('');
const [signUpPhone, setSignUpPhone] = useState('');
const [signUpPassword, setSignUpPassword] = useState('');
const [signUpConfirmPassword, setSignUpConfirmPassword] = useState('');
const [role, setRole] = useState('patient');

const [loginEmail, setLoginEmail] = useState('');
const [loginPassword, setLoginPassword] = useState('');

const navigate = useNavigate(); 

const sampleUsers = [
    { loginEmail: "admin@medilocker.com", loginPassword: "admin123", role: "admin" },
    { loginEmail: "kumaran@medilocker.com", loginPassword: "kumaran123", role: "patient" },
    { loginEmail: "john@medilocker.com", loginPassword: "john123", role: "doctor" }
];

function handleLogin(e) {
    e.preventDefault();

    const user = sampleUsers.find(user => user.loginEmail === loginEmail && user.loginPassword === loginPassword && user.role === role);

    if (user) {
        localStorage.setItem("isLoggedIn", "true");
        localStorage.setItem("userRole", user.role);
        localStorage.setItem("userEmail", user.loginEmail);

        console.log("Login Successful:", user);
        if(user.role === "admin"){
           navigate("/admindashboard");
        }else if (user.role === "patient"){
            navigate("/dashboard");
        }else{
