import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "../index.css";

const Header = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(ture);
  const [userRole, setUserRole] = useState(null);
  const navigate = useNavigate();

  // Fetch role from localStorage on mount
  useEffect(() => {
    const storedRole = localStorage.getItem("userRole");
    if (storedRole) {
      setUserRole(storedRole);
      setIsLoggedIn(true);
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("userRole");
    localStorage.removeItem("isLoggedIn");
    localStorage.removeItem("userEmail");
    setUserRole(null);
    setIsLoggedIn(false);
    navigate("/");
  };

  return (
    <div className="header">
      <nav className="navbar navbar-expand-lg shadow-sm">
        <div className="container-fluid">
          <a className="navbar-brand d-flex align-items-center fs-2 fw-bold" href="/home">
            <img src="/assets/HeaderLogo.png" alt="Logo" width="50" height="40" className="me-2" />
            MediLocker
          </a>
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse justify-content-center" id="navbarNav">
            <ul className="navbar-nav fs-5 fw-bold">
              <li className="nav-item"><a className="nav-link active" href="/">Home</a></li>
              <li className="nav-item"><a className="nav-link" href="/about">About Us</a></li>
              <li className="nav-item"><a className="nav-link" href="/contact">Contact Us</a></li>
            </ul>
          </div>
          <div className="d-flex ms-auto align-items-center">
            {!isLoggedIn ? (
              <>
                <a className="btn me-3 fw-bold rounded-pill" href="/login">Log In</a>
                <a className="btn btn-success px-4 rounded-pill text-black" href="/register">Sign Up</a>
              </>
            ) : (
              <div className="dropdown">
                <button className="btn btn-light dropdown-toggle rounded-pill" type="button" data-bs-toggle="dropdown">
                  <img width="24" height="24" src="https://img.icons8.com/material-sharp/24/gender-neutral-user.png" alt="user-icon"/>
                </button>
                <ul className="dropdown-menu dropdown-menu-end">
                  {userRole === "admin" && (
                    <>
                      <li><a className="dropdown-item" href="/admindashboard">Admin Dashboard</a></li>
                      <li><a className="dropdown-item" href="/manage-users">Manage Users</a></li>
                      <li><a className="dropdown-item" href="/manage-documents">Manage Documents</a></li>
                    </>
                  )}
                  {userRole === "doctor" && (
                    <>
                      <li><a className="dropdown-item" href="/doctordashboard">Doctor Dashboard</a></li>
                      <li><a className="dropdown-item" href="/patients">Patient Records</a></li>
                      <li><a className="dropdown-item" href="/appointments">Appointments</a></li>
                    </>
                  )}
                  {userRole === "patient" && (
                    <>
                      <li><a className="dropdown-item" href="/dashboard">My Dashboard</a></li>
                      <li><a className="dropdown-item" href="/profile">My Profile</a></li>
                      <li><a className="dropdown-item" href="/documents">My Documents</a></li>
                    </>
                  )}
                  <li><hr className="dropdown-divider" /></li>
                  <li><a className="dropdown-item text-danger" href="/" onClick={handleLogout}>Logout</a></li>
                </ul>
              </div>
            )}
          </div>
        </div>
      </nav>
    </div>
  );
};

export default Header;
