import React, { useState, useEffect } from "react";
import Header from "../components/Header";
import Footer from "../components/Footer";
import { getAllDocuments, downloadDocument } from "../api/DocumentService";
import { getPatientId } from "../api/PatientService";

const Documents = () => {
  const [activeTab, setActiveTab] = useState("ALL");
  const [searchTerm, setSearchTerm] = useState("");
  const [documents, setDocuments] = useState([]);
  const [showShareOptions, setShowShareOptions] = useState(false);
  const [currentFile, setCurrentFile] = useState("");

  const user = JSON.parse(localStorage.getItem("users"));
  const userId = user.user_id;

  const handleTabChange = (tab) => setActiveTab(tab);

  useEffect(() => {
    fetchDocuments();
  }, []);

  const fetchDocuments = async () => {
    try {
      const res = await getAllDocuments();
      const allDocs = res.data;

      const patient = await getPatientId(userId);
      const patientId = patient.data;

      const filteredDocs = allDocs.filter(
        (doc) => doc.patient.patient_id === patientId
      );

      setDocuments(filteredDocs);
      console.log(filteredDocs);
    } catch (err) {
      console.error("Error fetching documents:", err);
    }
  };

  const handleDownload = async (fileName) => {
    try {
      const response = await downloadDocument(fileName);
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement("a");
      link.href = url;
      link.setAttribute("download", fileName);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (err) {
      console.error("Error downloading document:", err);
    }
  };

  const handleShare = (fileName) => {
    setCurrentFile(fileName);
    setShowShareOptions(true);
  };

  const generateShareUrl = (fileName) => {
    return `http://localhost:8080/documents/view/${fileName}`; // Adjust this URL as per your backend endpoint
  };

  const shareViaWhatsApp = () => {
    const shareUrl = generateShareUrl(currentFile);
    const message = `Check out this document: ${shareUrl}`;
    window.open(`https://wa.me/?text=${encodeURIComponent(message)}`, "_blank");
    setShowShareOptions(false);
  };

  const shareViaSMS = () => {
    const shareUrl = generateShareUrl(currentFile);
    const message = `Check out this document: ${shareUrl}`;
    window.open(`sms:?body=${encodeURIComponent(message)}`, "_blank");
    setShowShareOptions(false);
  };

  const copyToClipboard = async () => {
    const url = generateShareUrl(currentFile);
    try {
      await navigator.clipboard.writeText(url);
      alert("URL copied to clipboard!");
    } catch (err) {
      console.error("Failed to copy:", err);
    }
    setShowShareOptions(false);
  };

  const getTabs = () => {
    const tabs = new Set();
    documents.forEach((doc) => tabs.add(doc.fileType.replace(".", "").toUpperCase()));
    return Array.from(tabs);
  };

  const filteredDocuments = documents.filter((doc) => {
    const matchesTab =
      activeTab === "ALL" || doc.fileType.replace(".", "").toUpperCase() === activeTab;
    const matchesSearch = doc.fileName.toLowerCase().includes(searchTerm.toLowerCase());
    return matchesTab && matchesSearch;
  });

  return (
    <div className="document">
      <Header />
      <section className="documentsection">
        <div className="container mt-4">
          <h2 className="mb-4 text-center">My Document Center</h2>

          <div className="d-flex justify-content-between align-items-center mb-4">
            <div>
              <button
                className={`btn button me-2 ${activeTab === "ALL" ? "btn-success" : "btn-outline-success"}`}
                onClick={() => handleTabChange("ALL")}
              >
                ALL
              </button>
              {getTabs().map((tab) => (
                <button
                  key={tab}
                  className={`btn button me-2 ${activeTab === tab ? "btn-success" : "btn-outline-success"}`}
                  onClick={() => handleTabChange(tab)}
                >
                  {tab}
                </button>
              ))}
            </div>
            <input
              type="text"
              className="form-control w-25"
              placeholder="Search documents..."
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>

          <div className="row justify-content-center">
            {filteredDocuments.map((doc, index) => (
              <div key={index} className="col-md-4 d-flex justify-content-center">
                <div className="card p-4 text-center shadow-sm mb-4" style={{ width: "18rem" }}>
                  <h6 className="mb-3">{doc.fileName}</h6>
                  <div>
                    <button
                      className="btn btn-warning me-2"
                      onClick={() => handleShare(doc.fileName)}
                    >
                      Share URL
                    </button>
                    <button
                      className="btn btn-success"
                      onClick={() => handleDownload(doc.fileName)}
                    >
                      Download
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>

          {/* Modal for Sharing Options */}
          {showShareOptions && (
            <div className="modal d-block" tabIndex="-1" style={{ backgroundColor: "rgba(0,0,0,0.5)" }}>
              <div className="modal-dialog modal-dialog-centered">
                <div className="modal-content">
                  <div className="modal-header">
                    <h5 className="modal-title">Share Document</h5>
                    <button type="button" className="btn-close" onClick={() => setShowShareOptions(false)}></button>
                  </div>
                  <div className="modal-body">
                    <p>Where do you want to share the document?</p>
                    <div className="d-flex justify-content-between">
                      <button className="btn btn-success" onClick={shareViaWhatsApp}>
                        WhatsApp
                      </button>
                      <button className="btn btn-info" onClick={shareViaSMS}>
                        SMS
                      </button>
                      <button className="btn btn-secondary" onClick={copyToClipboard}>
                        Copy URL
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </section>
      <Footer />
    </div>
  );
};

export default Documents;
