import axios from "axios";

const API_BASE = "http://localhost:8080/users";

export const registerUser = (userData) => {
  return axios.post(`${API_BASE}/addUser`, userData);
};

export const loginUser = (loginData) => {
  // return axios.post(`${API_BASE}/login`, loginData);
  const response = axios.post(`${API_BASE}/login`, loginData);
  const user = response.data;
  localStorage.setItem("user", JSON.stringify(user));
  return user;
};

export const emergencyLogin = (data) => {
  return axios.post(`${API_BASE}/emergenceLogin`, data);
};

export const getUserById = (userId) => {
  return axios.get(`${API_BASE}/getUser/${userId}`);
};

export const getAllUsers = () => {
  return axios.get(`${API_BASE}/getAllUser`);
};

export const updateUser = (userId, userData) => {
  return axios.put(`${API_BASE}/updateUser/${userId}`, userData);
};

export const deleteUser = (userId) => {
  return axios.delete(`${API_BASE}/deleteUser/${userId}`);
};
