import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "../index.css";

const Header = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(() => localStorage.getItem("isLoggedIn") === "false");
  const [role, setRole] = useState(() => localStorage.getItem("userRole") || "Patient"); // Default to "Patient"

  useEffect(() => {
    setRole(localStorage.getItem("userRole") || "Patient");
  }, [isLoggedIn]);

  const handleLogin = () => {
    setIsLoggedIn(false);
    localStorage.setItem("isLoggedIn", "true");
    // Simulate setting role (replace with actual role from API response)
    localStorage.setItem("userRole", "Doctor"); // Change to "Admin" or "Patient" as needed
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    localStorage.removeItem("isLoggedIn");
    localStorage.removeItem("userRole");
  };

  return (
    <div className="header">
      <nav className="navbar navbar-expand-lg shadow-sm">
        <div className="container-fluid">
          <Link className="navbar-brand d-flex align-items-center fs-2 fw-bold" to="/home">
            <img src="/assets/HeaderLogo.png" alt="MediLocker Logo" width="50" height="40" className="me-2" />
            MediLocker
          </Link>
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse justify-content-center" id="navbarNav">
            <ul className="navbar-nav fs-5 fw-bold">
              <li className="nav-item">
                <Link className="nav-link active" to="/">Home</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/about">About Us</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/contact">Contact Us</Link>
              </li>
            </ul>
          </div>
          <div className="d-flex ms-auto align-items-center">
            {!isLoggedIn ? (
              <>
                <Link className="btn me-3 fw-bold rounded-pill" to="/login" onClick={handleLogin}>Log In</Link>
                <Link className="btn btn-success px-4 rounded-pill text-black" to="/register">Sign Up</Link>
              </>
            ) : (
              <div className="dropdown">
                <button className="btn btn-light dropdown-toggle rounded-pill" type="button" data-bs-toggle="dropdown">
                  <img width="24" height="24" src="https://img.icons8.com/material-sharp/24/gender-neutral-user.png" alt="User Icon"/>
                </button>
                <ul className="dropdown-menu dropdown-menu-end">
                  {/* ✅ Common for All Users */}
                  <li><Link className="dropdown-item" to="/dashboard">My Dashboard</Link></li>
                  <li><Link className="dropdown-item" to="/profile">My Profile</Link></li>

                  {/* ✅ Role-Based Dropdown Items */}
                  {role === "Patient" && (
                    <>
                      <li><Link className="dropdown-item" to="/appointments">My Appointments</Link></li>
                      <li><Link className="dropdown-item" to="/records">My Medical Records</Link></li>
                    </>
                  )}
                  {role === "Doctor" && (
                    <>
                      <li><Link className="dropdown-item" to="/patients">Patient List</Link></li>
                      <li><Link className="dropdown-item" to="/schedule">Manage Schedule</Link></li>
                    </>
                  )}
                  {role === "Admin" && (
                    <>
                      <li><Link className="dropdown-item" to="/users">User Management</Link></li>
                      <li><Link className="dropdown-item" to="/settings">System Settings</Link></li>
                    </>
                  )}

                  <li><hr className="dropdown-divider" /></li>
                  <li>
                    <button className="dropdown-item text-danger" onClick={handleLogout}>Logout</button>
                  </li>
                </ul>
              </div>
            )}
          </div>
        </div>
      </nav>
    </div>
  );
};

export default Header;
