import React, { useEffect, useState } from "react";

const Profile = () => {


    const [profileImage, setProfileImage] = useState("");
  const [selectedFile, setSelectedFile] = useState(null);

  useEffect(() => {
    // Fetch the user's profile picture (replace USER_ID dynamically)
    axios.get(`http://localhost:8080/user/profile-picture/1`)
      .then((response) => {
        setProfileImage(response.data); // Set the image URL
      })
      .catch((error) => {
        console.error("Error fetching profile picture:", error);
      });
  }, []);

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleUpload = () => {
    if (!selectedFile) {
      alert("Please select an image to upload.");
      return;
    }

    const formData = new FormData();
    formData.append("file", selectedFile);

    // Upload new profile picture
    axios.post(`http://localhost:8080/user/upload-profile-picture/1`, formData, {
      headers: { "Content-Type": "multipart/form-data" }
    })
    .then((response) => {
      setProfileImage(response.data); // Update profile picture after upload
      alert("Profile picture updated successfully!");
    })
    .catch((error) => {
      console.error("Error uploading profile picture:", error);
      alert("Failed to upload profile picture.");
    });
  };


    return(
        <div className="profile">
            <div class="container text-center">
            <h2 className="text-center">My Profile</h2>
                <div class="row">
                    <div class="col">
                 
                    <div className="profile-card mx-auto text-center p-4">
                        <img
                        src={profileImage || "/assets/default-profile.png"} // Default image fallback
                        alt="Profile"
                        className="profile-img-lg"
                        />
                        <input type="file" className="form-control mt-3" onChange={handleFileChange} />
                        <button className="btn btn-primary mt-3" onClick={handleUpload}>Upload New Picture</button>
                    </div>
                    </div>
                    <div class="col">
                    Column
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Profile;
