import React, { useState,useEffect } from "react";
import Header from "../components/Header";
import Footer from "../components/Footer";
import { getPatient, addPatient } from "../api/PatientService";
import { getUserById,updateUser } from "../api/UserService";

const Profilepage = () => {
  // const patientId = localStorage.getItem("patientId"); 
  // const userId = localStorage.getItem("userId"); 
  const patientId = 4; 
  const userId = 6; 
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    name: "",
    emailId: "",
    dateOfBirth: "",
    phone: "",
    gender: "",
    address: "",
    height: "",
    weight: "",
    bloodGroup: "",
    bloodPressure: "",
    sugarLevel: "",
    bloodDonationDate : "",
  });

  useEffect(() => {
    if (patientId) {
      getPatient(patientId)
        .then((res) => {
          setFormData(res.data);
        })
        .catch((err) => {
          console.error("Failed to fetch patient data", err);
        });
    }
  }, [patientId]);

  useEffect(() => {
    if (userId) {
      getUserById(userId)
        .then((res) => {
          setFormData(res.data);
        })
        .catch((err) => {
          console.error("Failed to fetch user data", err);
        });
    }
  }, [userId]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };
  
  const handleEdit = () => setIsEditing(true);
  const handleSave = () => {
    // addPatient(patientId, formData)
    //   .then(() => {
    //     alert("Profile updated successfully!");
    //     setIsEditing(false);
    //   })
    //   .catch((err) => {
    //     console.error("Failed to update profile", err);
    //     alert("Failed to update profile.");
    //   });
    //   updateUser(userId, formData)
    //   .then(() => {
    //     alert("Profile updated successfully!");
    //     setIsEditing(false);
    //   })
    //   .catch((err) => {
    //     console.error("Failed to update profile", err);
    //     alert("Failed to update profile.");
    //   });
    Promise.all([
  addPatient(patientId, formData),
  updateUser(userId, formData)
])
  .then(() => {
    alert("Profile updated successfully!");
    setIsEditing(false);
  })
  .catch((err) => {
    console.error("Failed to update profile", err);
    alert("Failed to update profile.");
  });
  }
    
  const handleCancel = () => {
    setIsEditing(false);
    getPatient(patientId)
      .then((res) => {
        setFormData(res.data);
      })
      .catch((err) => console.error("Failed to reload patient data", err));
  }

  return (
    <div className="profile">
      <Header/>
        <section className="profilessection">
          <div className="container mt-3">
            <h2>My Profile</h2>
            <div className="d-flex justify-content-between align-items-center">
              <h4 className="mt-4 text-start">My Personal Information</h4>
              {!isEditing && <button className="btn btn-success" onClick={handleEdit}>Edit</button>}
            </div>
            <hr className="border border-3 border-success" />
            <div className="row g-3 mt-2">
              <div className="col-md-6">
                <div className="form-floating">
                  <input 
                    type="text" 
                    id="floatingName" 
                    name="name" 
                    value={formData.name} 
                    readOnly 
                    placeholder="Enter Patient Name"
                    required
                    autoComplete="off"
                    className="form-control"
                  />
                  <label htmlFor="floatingName">Name</label>
                </div>
              </div>
              <div className="col-md-6">
                <div className="form-floating">
                  <input 
                    type="date" 
                    name="DOB"
                    id="floatingDOB" 
                    value={formData.dob} 
                    readOnly 
                    placeholder="Enter Patient Date of Birth"
                    required
                    autoComplete="off"
                    className="form-control" 
                  />
                  <label htmlFor="floatingDOB">Date of Birth</label>
                </div>
              </div>
              <div className="col-md-6">
                <div className="form-floating">
                  <input 
                    type="email"  
                    id="floatingEmail" 
                    name="email" 
                    value={formData.email} 
                    onChange={handleChange} 
                    disabled={!isEditing} 
                    placeholder="Enter Patient Email"
                    required
                    autoComplete="off"
                    className="form-control"
                  />
                  <label htmlFor="floatingEmail">Email</label>
                </div>
              </div>
              <div className="col-md-6">
                <div className="form-floating">
                  <input 
                    type="text" 
                    id="floatingPhone"
                    name="phone" 
                    value={formData.phone} 
                    onChange={handleChange} 
                    disabled={!isEditing} 
                    placeholder="Enter Patient Phone"
                    required
                    autoComplete="off"
                    className="form-control"
                    />
                  <label htmlFor="floatingPhone">Phone</label>
                </div>
              </div>
              <div className="col-md-6">
                <div className="form-floating">
                  <input 
                    type="text" 
                    id="floatingGender" 
                    name="gender" 
                    value={formData.gender} 
                    onChange={handleChange} 
                    disabled={!isEditing} 
                    placeholder="Enter Patient Gender"
                    required
                    autoComplete="off"
                    className="form-control"
                  />
                  <label htmlFor="floatingGender">Gender</label>
                </div>
              </div>
              <div className="col-md-6">
                <div className="form-floating">
                  <input 
                    type="text" 
                    id="floatingAddress" 
                    name="address" 
                    value={formData.address} 
                    onChange={handleChange} 
                    disabled={!isEditing} 
                    placeholder="Enter Patient Address"
                    required
                    autoComplete="off"
                    className="form-control"                  
                  />
                  <label htmlFor="floatingAddress">Address</label>
                </div>
              </div>
            </div>
            
            <h4 className="mt-4 text-start">My Health Information</h4>
            <hr className="border border-3 border-success" />
            <div className="row g-3 mt-2">
              <div className="col-md-6">
                <div className="form-floating">
                  <input 
                    type="text" 
                    id="floatingHeight" 
                    name="height" 
                    value={formData.height} 
                    onChange={handleChange} 
                    disabled={!isEditing}
                    placeholder="Enter Patient Height"
                    required
                    autoComplete="off"
                    className="form-control"
                  />
                  <label htmlFor="floatingHeight">Height</label>
                </div>
              </div>
              <div className="col-md-6">
                <div className="form-floating">
                  <input 
                    type="text" 
                    id="floatingWeight" 
                    name="weight" 
                    value={formData.weight} 
                    onChange={handleChange} 
                    disabled={!isEditing} 
                    placeholder="Enter Patient Weight"
                    required
                    autoComplete="off"
                    className="form-control"
                  />
                  <label htmlFor="floatingWeight">Weight</label>
                </div>
              </div>
              <div className="col-md-6">
                <div className="form-floating">
                  <input 
                    type="text" 
                    id="floatingBloodGroup" 
                    name="bloodGroup" 
                    value={formData.bloodGroup} 
                    onChange={handleChange} 
                    disabled={!isEditing} 
                    placeholder="Enter Patient Blood Group"
                    required
                    autoComplete="off"
                    className="form-control"
                  />
                  <label htmlFor="floatingBloodGroup">Blood Group</label>
                </div>
              </div>
              <div className="col-md-6">
                <div className="form-floating">
                  <select
                      id="floatingBloodPressure"
                      name="bloodPressure"
                      value={formData.bloodPressure}
                      onChange={handleChange}
                      disabled={!isEditing}
                      className="form-select"
                      required>
                    <option value="">Select Blood Pressure</option>
                    <option value="Low">Low</option>
                    <option value="Normal">Normal</option>
                    <option value="High">High</option>
                  </select>
                  <label htmlFor="floatingBloodPressure">Blood Pressure</label>
                </div>
              </div>

              <div className="col-md-6">
                <div className="form-floating">
                  <select
                      id="floatingSugarLevel"
                      name="sugarLevel"
                      value={formData.sugarLevel}
                      onChange={handleChange}
                      disabled={!isEditing}
                      className="form-select"
                      required>
                    <option value="">Select Sugar Level</option>
                    <option value="Low">Low</option>
                    <option value="Normal">Normal</option>
                    <option value="High">High</option>
                  </select>
                  <label htmlFor="floatingSugarLevel">Sugar Level</label>
                </div>
              </div>
              <div className="col-md-6">
                <div className="form-floating">
                  <input 
                    type="date" 
                    id="floatingBloodDonationDate" 
                    name="bloodDonationDate" 
                    value={formData.bloodDonationDate} 
                    onChange={handleChange} 
                    disabled={!isEditing} 
                    placeholder="Enter Patient Blood Donation Date"
                    required
                    autoComplete="off"
                    className="form-control"
                  />
                  <label htmlFor="floatingBloodDonationDate">Blood Donation Date</label>
                </div>
              </div>
            </div>
            
            {isEditing && (
              <div className="mt-3">
                <button className="btn btn-success me-2" onClick={handleSave}>Save</button>
                <button className="btn btn-danger" onClick={handleCancel}>Cancel</button>
              </div>
            )}
          </div>
        </section>
      <Footer/>
    </div>
  );
};

export default Profilepage;
