import React, { useState } from "react";

const LoginForm = ()=>{


    const [email,setEmail] = useState('')
    const [password,setPassword] = useState('')

    function handleEmail (e){
        setEmail(e.target.value);
    }

    function handlePassword(e){
        setPassword(e.target.value);
    }

    function handleLogin(e){
        e.preventDefault();

        const user ={email,password};
        console.log(user);
    }

    return(
        <div className="loginform card">
            <div className="card-body">
                <h2 className="card-title p-3">Welcome Back!</h2>
                <form action="#">
                    <div className="form-floating mb-3">
                        <input type="email"
                            id="floatingInput"
                            name="email"
                            value={email}
                            onChange={handleEmail}
                            placeholder="Enter your email"
                            required
                            autoComplete="off"
                            className="form-control"/>
                        <label for="floatingInput">Email</label>
                    </div>
                    <div className="form-floating">
                        <input type="password" 
                            id="floatingPassword"
                            name="password"
                            value={password}
                            onChange={handlePassword}
                            placeholder="Enter your Password"
                            required
                            autoComplete="off"
                            className="form-control"  />
                        <label for="floatingPassword">Password</label>
                    </div>
                    <div class="col-12 mt-4">
                        <button type="submit" className="btn btn-success w-100" onClick={handleLogin}>Login</button>
                    </div>
                    <br />
                   
                    <p className="form-text">
                       <a href="/LoginForm" className="form-link text-black">Forgot Password?</a>
                    </p>
                    <p className="form-text">
                        Don't have an account? <a href="/register" className="form-link text-black">Create an Account</a>
                    </p>
                </form>
            </div>
        </div>
    )
}

export default LoginForm;
