// export default DoctorProfile;

const DoctorProfile = () => {
    const [isEditing, setIsEditing] = useState(false);
    const [userId,setUserId] = useState();
    const [doctorId,SetDoctorId] = useState();

    const [formData, setFormData] = useState({
        name: "", 
        dateOfBirth: "", 
        emailId: "", 
        phone: "", 
        gender: "", 
        address: "", 
        password: "",
        role: "", 
        specialization: "",
        yoe: "",
      });

    useEffect(() => {
          const fetchData = async () => {
            try {
                const storedUser = JSON.parse(localStorage.getItem("users"));

                const userId = storedUser.user_id;
                setUserId(userId);
                console.log("UserId :",userId);

                const doctor = await getDoctorId(userId);
                const doctorId = doctor.data;
                SetDoctorId(doctorId);
                console.log("DoctorID :",doctorId)

            const response = await getDoctor(doctorId);
            setFormData(response.data);
            console.log("Doctor Data :",response.data);
            } catch (err) {
              console.error("Failed to fetch doctor or user data", err);
            }
          };
      
          fetchData();
      }, []);
      

    const handleChange = (e) => {setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleEdit = () => setIsEditing(true);

    const handleSave = () => {
        try{
            const updatedUser = {
                name: formData.name,
                emailId: formData.emailId,
                dateOfBirth: formData.dateOfBirth,
                phone: formData.phone,
                gender: formData.gender,
                address: formData.address, 
                password: formData.password,
                role: formData.role,
              };
          
              const updatedDoctor = {
                specialization: formData.specialization,
                yoe: formData.yoe,
              };
          updateDoctor(doctorId,updatedDoctor);
          updateUser(userId,updatedUser);
          setIsEditing(false);
          Swal.fire("Success","Profile updated successfully!","success");
        }catch(error){
          Swal.fire("Error","Failed to update profile.","error");
        }
    };

    const handleCancel = () => {
        setIsEditing(false);
        Promise.all([getDoctor(doctorId), getUserById(userId)])
        .then(([doctorRes, userRes]) => {
          setDoctorData(doctorRes.data);
          setUserData(userRes.data);
        })
        .catch((err) => {
          console.error("Failed to reload data", err);
        });
    };

  return (
    <div className="doctorprofile">
        <Header/>
            <section className="doctorprofilesection">
                <div className="container mt-3">
                <h2>My Profile</h2>
                <div className="d-flex justify-content-between align-items-center">
                    <h4 className="mt-4">Personal Information</h4>
                    {!isEditing && <button className="btn btn-success" onClick={handleEdit}>Edit</button>}
                </div>
                <hr className="border border-3 border-success" />

                <div className="row g-3 mt-2">
                    <div className="col-md-6">
                    <div className="form-floating">
                        <input 
                            type="text"  
                            id="floatingName"
                            name="name"
                            value={formData.name} 
                            placeholder="Enter Doctor Name"
                            required
                            autoComplete="off"
                            readOnly
                            className="form-control"
                        />
                        <label htmlFor="floatingName">Name</label>
                    </div>
                    </div>
                    <div className="col-md-6">
                    <div className="form-floating">
                        <input 
                            type="date" 
                            id="floatingDOB"
                            name="DOB"
                            value={formData.dateOfBirth} 
                            placeholder="Enter Doctor Date of Birth"
                            required
                            autoComplete="off"
                            readOnly
                            className="form-control"
                        />
                        <label htmlFor="floatingDOB">Date of Birth</label>
                    </div>
                    </div>
                    <div className="col-md-6">
                    <div className="form-floating">
                        <input 
                            type="email" 
                            id="floatingEmail" 
                            name="email" 
                            value={formData.emailId} 
                            onChange={handleChange} 
                            disabled={!isEditing}
                            placeholder="Enter Doctor Email"
                            required
                            autoComplete="off"
                            className="form-control" 
                        />
                        <label htmlFor="floatingEmail">Email</label>
                    </div>
                    </div>
                    <div className="col-md-6">
                    <div className="form-floating">
                        <input 
                            type="text" 
                            id="floatingPhone" 
                            name="phone" 
                            value={formData.phone} 
                            onChange={handleChange} 
                            disabled={!isEditing}
                            placeholder="Enter Doctor Phone"
                            required
                            autoComplete="off"
                            className="form-control"
                        />
                        <label htmlFor="floatingPhone">Phone</label>
                    </div>
                    </div>
                    <div className="col-md-6">
                    <div className="form-floating">
                        <select
                            id="floatingGender"
                            name="gender"
                            value={formData.gender}
                            onChange={handleChange}
                            disabled={!isEditing}
                            placeholder="Enter Doctor Gender"
                            required
                            autoComplete="off"
                            className="form-select">
                            <option value="">Select Gender</option>
                            <option value="Male">Male</option>
                            <option value="Female">Female</option>
                            <option value="Other">Other</option>
                        </select>
                        <label htmlFor="floatingGender">Gender</label>
                    </div>
                    </div>
                    <div className="col-md-6">
                    <div className="form-floating">
                        <input 
                            type="text" 
                            id="floatingAddress" 
                            name="address" 
                            value={formData.address} 
                            onChange={handleChange} 
                            disabled={!isEditing} 
                            placeholder="Enter Doctor Address"
                            required
                            autoComplete="off"
                            className="form-control"
                        />
                        <label htmlFor="floatingAddress">Address</label>
                    </div>
                    </div>
                </div>

                <h4 className="mt-4">My Specialty & Experience</h4>
                <hr className="border border-3 border-primary" />
                
                <div className="row g-3 mt-2">
                    <div className="col-md-6">
                    <div className="form-floating">
                        <input 
                            type="text" 
                            id="floatingSpecialization" 
                            name="specialization" 
                            value={formData.specialization} 
                            onChange={handleChange} 
                            disabled={!isEditing} 
                            placeholder="Enter Doctor Specialization"
                            required
                            autoComplete="off"
                            className="form-control"/>
                        <label htmlFor="floatingSpecialization">Specialization</label>
                    </div>
                    </div>
                    <div className="col-md-6">
                    <div className="form-floating">
                        <input 
                            type="text" 
                            id="floatingyoe" 
                            name="yoe" 
                            value={formData.yoe} 
                            onChange={handleChange} 
                            disabled={!isEditing} 
                            placeholder="Enter Doctor yoe"
                            required
                            autoComplete="off"
                            className="form-control"/>
                        <label htmlFor="floatingyoe">Years of Experience</label>
                    </div>
                    </div>
                </div>
                {isEditing && (
                    <div className="mt-3">
                    <button className="btn btn-success me-2" onClick={handleSave}>Save</button>
                    <button className="btn btn-danger" onClick={handleCancel}>Cancel</button>
                    </div>
                )}
                </div>
            </section>
        <Footer/>
    </div>
  );
};

export default DoctorProfile;
