import React,{ useState, useEffect } from "react";
import Header from "../components/Header";
import Footer from "../components/Footer";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import axios from "axios";
import L from "leaflet";


import iconUrl from "leaflet/dist/images/marker-icon.png";
import iconShadow from "leaflet/dist/images/marker-shadow.png";

delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require("leaflet/dist/images/marker-icon-2x.png"),
  iconUrl: require("leaflet/dist/images/marker-icon.png"),
  shadowUrl: require("leaflet/dist/images/marker-shadow.png"),
});

const Hospital = () => {

const [location, setLocation] = useState({ lat: 13.0827, lng: 80.2707 }); // Default: Tamil Nadu
const [hospitals, setHospitals] = useState([]);

  useEffect(() => {
    // Get current location
    navigator.geolocation.getCurrentPosition(
      (position) => {
        setLocation({
          lat: position.coords.latitude,
          lng: position.coords.longitude,
        });
        fetchHospitals(position.coords.latitude, position.coords.longitude);
      },
      () => {
        // If denied, use default location
        fetchHospitals(location.lat, location.lng);
      }
    );
  }, []);

  const fetchHospitals = async (lat, lng) => {
    try {
      const response = await axios.get(
        `https://nominatim.openstreetmap.org/search?format=json&q=hospital+near+${lat},${lng}`
      );
      setHospitals(response.data);
    } catch (error) {
      console.error("Error fetching hospitals:", error);
    }
  };


    return(
        <div className="hospital">
            <Header/>
            {/* <section className="hospitalsection">
                <iframe src="https://www.google.com/maps/embed?pb=!1m16!1m12!1m3!1d31162.166167737803!2d80.15402188417629!3d12.498203435207312!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!2m1!1snearby%20hospital!5e0!3m2!1sen!2sin!4v1743770320583!5m2!1sen!2sin" 
                width="600" height="450"  allowfullscreen="" loading="lazy" referrerpolicy="no-referrer-when-downgrade"></iframe>
            </section> */}
            <section className="hospitalsection">
            <h3>Find NearBy Hospitals</h3>
            <br />
            <MapContainer center={[location.lat, location.lng]} zoom={10} style={{ height: "500px", width: "100%" }}>
                <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
                <Marker position={[location.lat, location.lng]}>
                    <Popup>Your Location</Popup>
                </Marker>
                {hospitals.map((hospital, index) => (
                    <Marker key={index} position={[hospital.lat, hospital.lon]}>
                    <Popup>{hospital.display_name}</Popup>
                    </Marker>
                ))}
            </MapContainer>
            </section> 
            <Footer/>
        </div>
    )
}

export default Hospital;
