import React, { useState } from "react";

const Dashboard = ({ userName }) => {
  const [selectedDoctor, setSelectedDoctor] = useState("");
  const [appointmentConfirmed, setAppointmentConfirmed] = useState(false);

  // Default doctor list (Replace this with API data later)
  const doctors = [
    "Dr. John Smith - Cardiologist",
    "Dr. Emily Brown - Dermatologist",
    "Dr. Sarah Lee - Neurologist",
    "Dr. Michael Johnson - Orthopedic",
  ];

  const handleChange = (e) => {
    setSelectedDoctor(e.target.value);
  };

  const handleBookAppointment = () => {
    if (selectedDoctor) {
      setAppointmentConfirmed(true);
    }
  };

  return (
    <div className="container mt-4">
      <h2 className="text-start">Dashboard</h2>
      <h3 className="text-start">Welcome, {userName}!</h3>
      
      {/* Booking Section */}
      <div className="mt-4">
        <h4>Book an Appointment</h4>
        <hr className="border border-3 border-success" />

        <div className="row g-3 mt-2">
          <div className="col-md-6">
            <div className="form-floating">
              <select
                className="form-control"
                id="floatingDoctor"
                value={selectedDoctor}
                onChange={handleChange}
              >
                <option value="">Select a Doctor</option>
                {doctors.map((doctor, index) => (
                  <option key={index} value={doctor}>
                    {doctor}
                  </option>
                ))}
              </select>
              <label htmlFor="floatingDoctor">Choose a Doctor</label>
            </div>
          </div>
          <div className="col-md-6 d-flex align-items-center">
            <button
              className="btn btn-success"
              onClick={handleBookAppointment}
              disabled={!selectedDoctor}
            >
              Book Appointment
            </button>
          </div>
        </div>

        {appointmentConfirmed && (
          <div className="alert alert-success mt-3" role="alert">
            Appointment booked successfully with {selectedDoctor}!
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
