import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../index.css";

const LoginRegister = () => {
  const [isSignUp, setIsSignUp] = useState(false);
  const navigate = useNavigate();
  
  const handleToggle = () => {
    setIsSignUp((prev) => !prev);
  };

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('patient');
  const [error, setError] = useState('');
  const [name, setName] = useState('');
  const [dob, setDob] = useState('');
  const [phone, setPhone] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');

  const sampleUsers = [
    { email: "admin@medilocker.com", password: "admin123", role: "admin" },
    { email: "kumaran@medilocker.com", password: "kumaran123", role: "patient" },
    { email: "john@medilocker.com", password: "john123", role: "doctor" }
  ];

  function handleLogin(e) {
    e.preventDefault();
    const user = sampleUsers.find(user => user.email === email && user.password === password && user.role === role);
    if (user) {
      localStorage.setItem("isLoggedIn", "true");
      localStorage.setItem("userRole", user.role);
      localStorage.setItem("userEmail", user.email);
      navigate(user.role === "admin" ? "/admindashboard" : user.role === "patient" ? "/dashboard" : "/doctordashboard");
    } else {
      setError("Invalid email, password, or role selection.");
    }
  }

  function handleSubmit(e) {
    e.preventDefault();
    console.log({ name, email, dob, phone, password, confirmPassword, role });
  }

  return (
    <section className="auth-container">
      <div className={`wrapper ${isSignUp ? "animated-signup" : "animated-signin"}`}>
        {/* Sign Up Form */}
        <div className="registerform card sign-up">
          <div className="card-body">
            <h2 className="card-title text-center">Create an Account</h2>
            <form onSubmit={handleSubmit}>
              <input type="text" value={name} onChange={(e) => setName(e.target.value)} placeholder="Name" required className="form-control mb-3" />
              <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email" required className="form-control mb-3" />
              <input type="date" value={dob} onChange={(e) => setDob(e.target.value)} required className="form-control mb-3" />
              <input type="tel" value={phone} onChange={(e) => setPhone(e.target.value)} placeholder="Phone" required className="form-control mb-3" />
              <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Password" required className="form-control mb-3" />
              <input type="password" value={confirmPassword} onChange={(e) => setConfirmPassword(e.target.value)} placeholder="Confirm Password" required className="form-control mb-3" />
              <div className="d-flex justify-content-between mb-3">
                <label><input type="radio" value="doctor" checked={role === "doctor"} onChange={(e) => setRole(e.target.value)} /> Doctor</label>
                <label><input type="radio" value="patient" checked={role === "patient"} onChange={(e) => setRole(e.target.value)} /> Patient</label>
              </div>
              <button type="submit" className="btn btn-success w-100">Register</button>
              <p className="text-center mt-3">Already have an account? <span onClick={handleToggle} className="toggle-link">Login</span></p>
            </form>
          </div>
        </div>
        {/* Login Form */}
        <div className="loginform card sign-in">
          <div className="card-body">
            <h2 className="card-title text-center">Welcome Back!</h2>
            <form onSubmit={handleLogin}>
              <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email" required className="form-control mb-3" />
              <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Password" required className="form-control mb-3" />
              <div className="d-flex justify-content-between mb-3">
                <label><input type="radio" value="admin" checked={role === "admin"} onChange={(e) => setRole(e.target.value)} /> Admin</label>
                <label><input type="radio" value="doctor" checked={role === "doctor"} onChange={(e) => setRole(e.target.value)} /> Doctor</label>
                <label><input type="radio" value="patient" checked={role === "patient"} onChange={(e) => setRole(e.target.value)} /> Patient</label>
              </div>
              {error && <p className="text-danger">{error}</p>}
              <button type="submit" className="btn btn-success w-100">Login</button>
              <p className="text-center mt-3"><a href="/forgotpassword">Forgot Password?</a></p>
              <p className="text-center">Don't have an account? <span onClick={handleToggle} className="toggle-link">Sign Up</span></p>
            </form>
          </div>
        </div>
      </div>
    </section>
  );
};

export default LoginRegister;
