import React, { useState } from "react";
// import "bootstrap/dist/css/bootstrap.min.css";

const Document = () => {
  const [activeTab, setActiveTab] = useState("documents");
  const [searchTerm, setSearchTerm] = useState("");

  const renderContent = () => {
    switch (activeTab) {
      case "documents":
        return <p>List of Documents</p>;
      case "xrays":
        return <p>List of X-rays</p>;
      case "prescriptions":
        return <p>List of Prescriptions</p>;
      default:
        return null;
    }
  };

  return (
    <div className="document">
        <section className="documentsection">
  
    <div className="container mt-4">
      <h2 className="mb-4">My Document Center</h2>
      <div className="row">
        {/* Sidebar */}
        <div className="col-md-3">
          <div className="list-group">
            <button
              className={`list-group-item list-group-item-action ${activeTab === "documents" && "active"}`}
              onClick={() => setActiveTab("documents")}
            >
              Documents
            </button>
            <button
              className={`list-group-item list-group-item-action ${activeTab === "xrays" && "active"}`}
              onClick={() => setActiveTab("xrays")}
            >
              X-rays
            </button>
            <button
              className={`list-group-item list-group-item-action ${activeTab === "prescriptions" && "active"}`}
              onClick={() => setActiveTab("prescriptions")}
            >
              Prescriptions
            </button>
          </div>
        </div>

        {/* Main Content */}
        <div className="col-md-6">
          <div className="card p-3">
            {renderContent()}
          </div>
        </div>

        {/* Search Bar */}
        <div className="col-md-3">
          <input
            type="text"
            className="form-control"
            placeholder="Search documents..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
      </div>
    </div>
              
    </section>
    </div>
  );
};

export default Document;
