import React, { useState, useEffect } from "react";
import Header from "../components/Header";
import Footer from "../components/Footer";
import { getAllDocuments,downloadDocument,viewDocument } from "../api/DocumentService";

const Documents = () => {
  const [activeTab, setActiveTab] = useState("ALL");
  const [searchTerm, setSearchTerm] = useState("");
  const [documents, setDocuments] = useState([]);

  const user = JSON.parse(localStorage.getItem("users"));
  const userName= user.name;

  const handleTabChange = (tab) => setActiveTab(tab);

  useEffect(() => {
    fetchDocuments();
  }, []);

  const fetchDocuments = async () => {
    try {
      // const res = await getAllDocuments();
      setDocuments(res.data);
      // console.log(res.data);

      // const fileName = res.data.fileName;
      // console.log(fileName);
      // const namePart = fileName.split("__");
      // const name = namePart[0];
      // console.log(name);
      const res = await getAllDocuments().then(res => {
        res.data.forEach(doc => {
          console.log("Full fileName:", doc.fileName);
          const namePart = doc.fileName.split("__");
          const name = namePart[0];
          console.log("Extracted name:", name);
        });
      });

      const filteredDocuments = res.data.filter(doc => doc.name === userName);
      console.log(filteredDocuments);


    } catch (err) {
      console.error("Error fetching documents:", err);
    }
  };

  const handleView = async (fileName) => {
    try {
      const response = await viewDocument(fileName);
      const blob = new Blob([response.data]);
      const fileURL = URL.createObjectURL(blob);
      window.open(fileURL);
    } catch (err) {
      console.error("Error viewing document:", err);
    }
  };

  const handleDownload = async (fileName) => {
    try {
      const response = await downloadDocument(fileName);
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement("a");
      link.href = url;
      link.setAttribute("download", fileName);
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (err) {
      console.error("Error downloading document:", err);
    }
  };

  const getTabs = () => {
    const tabs = new Set();
    documents.forEach((doc) => tabs.add(doc.fileType.replace(".", "").toUpperCase()));
    return Array.from(tabs);
  };

  const filteredDocuments = documents.filter((doc) => {
    const matchesTab =
      activeTab === "ALL" || doc.fileType.replace(".", "").toUpperCase() === activeTab;
    const matchesSearch = doc.fileName.toLowerCase().includes(searchTerm.toLowerCase());
    return matchesTab && matchesSearch;
  });

  return (
    <div className="document">
      <Header />
      <section className="documentsection">
        <div className="container mt-4">
          <h2 className="mb-4 text-center">My Document Center</h2>

          <div className="d-flex justify-content-between align-items-center mb-4">
            <div>
              <button
                className={`btn me-2 ${activeTab === "ALL" ? "btn-success" : "btn-outline-success"}`}
                onClick={() => handleTabChange("ALL")}
              >
                ALL
              </button>
              {getTabs().map((tab) => (
                <button
                  key={tab}
                  className={`btn me-2 ${activeTab === tab ? "btn-success" : "btn-outline-success"}`}
                  onClick={() => handleTabChange(tab)}
                >
                  {tab}
                </button>
              ))}
            </div>
            <input
              type="text"
              className="form-control w-25"
              placeholder="Search documents..."
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>

          <div className="row justify-content-center">
            {filteredDocuments.map((doc, index) => (
              <div key={index} className="col-md-4 d-flex justify-content-center">
                <div className="card p-4 text-center shadow-sm mb-4" style={{ width: "18rem" }}>
                  <h6 className="mb-3">{doc.fileName}</h6>
                  <div>
                    <button className="btn btn-primary me-2" onClick={() => handleView(doc.fileName)}>
                      View
                    </button>
                    <button className="btn btn-success" onClick={() => handleDownload(doc.fileName)}>
                      Download
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>
      <Footer />
    </div>
  );
};

export default Documents;
