import React, { useState } from "react";

const LoginForm = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("admin"); // Default role

  function handleEmail(e) {
    setEmail(e.target.value);
  }

  function handlePassword(e) {
    setPassword(e.target.value);
  }

  function handleRoleChange(e) {
    setRole(e.target.value);
  }

  function handleLogin(e) {
    e.preventDefault();
    const user = { email, password, role };
    console.log(user);
  }

  return (
    <div className="loginform card">
      <div className="card-body">
        <h2 className="card-title p-3">Welcome Back!</h2>
        <form action="#">
          <div className="form-floating mb-3">
            <input
              type="email"
              id="floatingInput"
              name="email"
              value={email}
              onChange={handleEmail}
              placeholder="Enter your email"
              required
              autoComplete="off"
              className="form-control"
            />
            <label htmlFor="floatingInput">Email</label>
          </div>
          <div className="form-floating mb-3">
            <input
              type="password"
              id="floatingPassword"
              name="password"
              value={password}
              onChange={handlePassword}
              placeholder="Enter your Password"
              required
              autoComplete="off"
              className="form-control"
            />
            <label htmlFor="floatingPassword">Password</label>
          </div>
          
          <div className="mb-3">
            <label className="form-label">Select Role:</label>
            <div>
              <input
                type="radio"
                id="admin"
                name="role"
                value="admin"
                checked={role === "admin"}
                onChange={handleRoleChange}
              />
              <label htmlFor="admin" className="ms-2">Admin</label>
            </div>
            <div>
              <input
                type="radio"
                id="patient"
                name="role"
                value="patient"
                checked={role === "patient"}
                onChange={handleRoleChange}
              />
              <label htmlFor="patient" className="ms-2">Patient</label>
            </div>
            <div>
              <input
                type="radio"
                id="doctor"
                name="role"
                value="doctor"
                checked={role === "doctor"}
                onChange={handleRoleChange}
              />
              <label htmlFor="doctor" className="ms-2">Doctor</label>
            </div>
          </div>
          
          <div className="col-12 mt-4">
            <button
              type="submit"
              className="btn btn-success w-100"
              onClick={handleLogin}
            >
              Login
            </button>
          </div>
          <br />
          <p className="form-text">
            <a href="/LoginForm" className="form-link text-black">Forgot Password?</a>
          </p>
          <p className="form-text">
            Don't have an account? <a href="/register" className="form-link text-black">Create an Account</a>
          </p>
        </form>
      </div>
    </div>
  );
};

export default LoginForm;
