import React, { useState } from "react";
import Header from "../components/Header";
import Footer from "../components/Footer";

const Documents = () => {
  const [activeTab, setActiveTab] = useState("Documents");
  const [searchTerm, setSearchTerm] = useState("");

  const data = {
    Documents: [
      { name: "Medical Report.pdf", url: "#" },
      { name: "Lab Test Results.docx", url: "#" },
      { name: "Health Summary.txt", url: "#" },
    ],
    Xrays: [
      { name: "Chest X-ray.png", url: "#" },
      { name: "Dental X-ray.jpg", url: "#" },
      { name: "Spine MRI.jpeg", url: "#" },
    ],
    Prescriptions: [
      { name: "Blood Pressure Medicine.pdf", url: "#" },
      { name: "Diabetes Treatment.docx", url: "#" },
    ],
  };

  const handleTabChange = (tab) => setActiveTab(tab);

  return (
    <div className="document">
      <Header />
      <section className="documentsection">
        <div className="container mt-4">
          <h2 className="mb-3">My Document Center</h2>
          <div className="d-flex justify-content-between align-items-center mb-3">
            <div>
              {Object.keys(data).map((tab) => (
                <button
                  key={tab}
                  className={`btn me-2 ${activeTab === tab ? "btn-success" : "btn-outline-success"}`}
                  onClick={() => handleTabChange(tab)}
                >
                  {tab}
                </button>
              ))}
            </div>
            <input
              type="text"
              className="form-control w-25"
              placeholder="Search documents..."
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
          <div className="row">
            {data[activeTab]
              .filter((doc) => doc.name.toLowerCase().includes(searchTerm.toLowerCase()))
              .map((doc, index) => (
                <div key={index} className="col-md-4">
                  <div className="card mb-3 shadow-sm">
                    <div className="card-body text-center">
                      <h6 className="card-title">{doc.name}</h6>
                      <div className="d-flex justify-content-around mt-2">
                        <a href={doc.url} target="_blank" rel="noopener noreferrer" className="btn btn-primary btn-sm">
                          View
                        </a>
                        <a href={doc.url} download className="btn btn-success btn-sm">
                          Download
                        </a>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
          </div>
        </div>
      </section>
      <Footer />
    </div>
  );
};

export default Documents;
