import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
// import "../style.css"; 

const LoginRegister = () => {
  const [isSignUp, setIsSignUp] = useState(false);

  const handleToggle = () => {
    setIsSignUp((prev) => !prev);
  };
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('patient');
  const [error, setError] = useState('');

  const navigate = useNavigate(); 


  const sampleUsers = [
      { email: "admin@medilocker.com", password: "admin123", role: "admin" },
      { email: "kumaran@medilocker.com", password: "kumaran123", role: "patient" },
      { email: "john@medilocker.com", password: "john123", role: "doctor" }
  ];

  function handleLogin(e) {
      e.preventDefault();

      const user = sampleUsers.find(user => user.email === email && user.password === password && user.role === role);

      if (user) {
          localStorage.setItem("isLoggedIn", "true");
          localStorage.setItem("userRole", user.role);
          localStorage.setItem("userEmail", user.email);

          console.log("Login Successful:", user);
          if(user.role === "admin"){
              navigate("/admindashboard");
          }else if (user.role === "patient"){
              navigate("/dashboard");
          }else{
              navigate("/doctordashboard");
          }           
      } else {
          setError("Invalid email, password, or role selection.");
      }
  }

  // sign up
    const [name, setName] = useState('');
      // const [email, setEmail] = useState('');
      const [dob, setDob] = useState('');
      const [phone, setPhone] = useState('');
      // const [password, setPassword] = useState('');
      const [confirmPassword, setConfirmPassword] = useState('');
      // const [role, setRole] = useState('patient'); 
  
      function handleSubmit(e) {
          e.preventDefault();
          const user = { name, email, dob, phone, password, confirmPassword, role };
          console.log(user);
      }
      
  return (
    <section className="mt-5 pt-5">
    <div className={`wrapper ${isSignUp ? "animated-signup" : "animated-signin"}`}>
      <div className="form-container card sign-up">
        {/* <form>
          <h2>Sign Up</h2>
          <div className="form-group">
            <input type="text" required />
            <i className="fas fa-user"></i>
            <label>Username</label>
          </div>
          <div className="form-group">
            <input type="date" required />
          </div>
          <div className="form-group">
            <input type="email" required />
            <i className="fas fa-at"></i>
            <label>Email</label>
          </div>
          <div className="form-group">
            <input type="password" required />
            <i className="fas fa-lock"></i>
            <label>Password</label>
          </div>
          <div className="form-group">
            <input type="password" required />
            <i className="fas fa-lock"></i>
            <label>Confirm Password</label>
          </div>
          <button type="submit" className="btn">Sign Up</button>
          <div className="link">
            <p>Already have an account? <span onClick={handleToggle} className="signin-link">Login</span></p>
          </div>
        </form> */}
         <div className="card-body">
                <h2 className="card-title p-3">Create an Account</h2>
                <form>
                    <div className="form-floating mb-3">
                        <input type="text"
                            id="floatingName"
                            name="name"
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                            placeholder="Enter your Name"
                            required
                            autoComplete="off"
                            className="form-control" />
                        <label htmlFor="floatingName">Name</label>
                    </div>
                    <div className="form-floating mb-3">
                        <input type="email"
                            id="floatingEmail"
                            name="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            placeholder="Enter your Email"
                            required
                            autoComplete="off"
                            className="form-control" />
                        <label htmlFor="floatingEmail">Email</label>
                    </div>
                    <div className="form-floating mb-3">
                        <input type="date"
                            id="floatingDate"
                            name="dob"
                            value={dob}
                            onChange={(e) => setDob(e.target.value)}
                            required
                            autoComplete="off"
                            className="form-control" />
                        <label htmlFor="floatingDate">Date of Birth</label>
                    </div>
                    <div className="form-floating mb-3">
                        <input type="tel"
                            id="floatingPhone"
                            name="phone"
                            value={phone}
                            onChange={(e) => setPhone(e.target.value)}
                            placeholder="Enter your Phone"
                            required
                            autoComplete="off"
                            className="form-control" />
                        <label htmlFor="floatingPhone">Phone</label>
                    </div>
                    <div className="form-floating mb-3">
                        <input type="password"
                            id="floatingPassword"
                            name="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            placeholder="Enter your Password"
                            required
                            autoComplete="off"
                            className="form-control" />
                        <label htmlFor="floatingPassword">Password</label>
                    </div>
                    <div className="form-floating mb-3">
                        <input type="password"
                            id="floatingConfirmPassword"
                            name="confirmPassword"
                            value={confirmPassword}
                            onChange={(e) => setConfirmPassword(e.target.value)}
                            placeholder="Confirm your Password"
                            required
                            autoComplete="off"
                            className="form-control" />
                        <label htmlFor="floatingConfirmPassword">Confirm Password</label>
                    </div>
                    <div className="mb-3 p-2">
                        <label className="form-label d-block fw-medium text-start">Select Role:</label>
                        <div className="d-flex align-items-center">
                            <input type="radio" 
                                id="doctor" 
                                name="role" 
                                value="doctor"
                                checked={role === "doctor"} 
                                onChange={(e) => setRole(e.target.value)}
                                className="me-1" />
                            <label htmlFor="doctor" className="me-3">Doctor</label>

                            <input type="radio" 
                                id="patient" 
                                name="role" 
                                value="patient"
                                checked={role === "patient"} 
                                onChange={(e) => setRole(e.target.value)}
                                className="me-1" />
                            <label htmlFor="patient">Patient</label>
                        </div>
                    </div>
                    <div className="col-12 mt-4">
                        <button type="submit" className="btn btn-success w-100" onClick={handleSubmit}>Register</button>
                    </div>
                    <br />
                    <p className="form-text">
                        Already have an account? <a href="/login" className="form-link text-black">Login here</a>
                    </p>
                </form>
            </div>
      </div>
      <div className="form-container card sign-in">
        {/* <form>
          <h2>Login</h2>
          <div className="form-group">
            <input type="text" required />
            <i className="fas fa-user"></i>
            <label>Username</label>
          </div>
          <div className="form-group">
            <input type="password" required />
            <i className="fas fa-lock"></i>
            <label>Password</label>
          </div>
          <div className="forgot-pass">
            <a href="#">Forgot Password?</a>
          </div>
          <button type="submit" className="btn">Login</button>
          <div className="link">
            <p>Don't have an account? <span onClick={handleToggle} className="signup-link">Sign Up</span></p>
          </div>
        </form> */}
         <div className="card-body">
                <h2 className="card-title p-3">Welcome Back!</h2>
                <form onSubmit={handleLogin}>
                    <div className="form-floating mb-3">
                        <input type="email"
                            id="floatingInput"
                            name="email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            placeholder="Enter your email"
                            required
                            autoComplete="off"
                            className="form-control" />
                        <label htmlFor="floatingInput">Email</label>
                    </div>
                    <div className="form-floating mb-3">
                        <input type="password"
                            id="floatingPassword"
                            name="password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            placeholder="Enter your Password"
                            required
                            autoComplete="off"
                            className="form-control" />
                        <label htmlFor="floatingPassword">Password</label>
                    </div>
                    <div className="mb-3 p-2">
                        <label className="form-label d-block fw-medium text-start">Select Role:</label>
                        <div className="d-flex align-items-center">
                            <input type="radio"
                                id="admin"
                                name="role"
                                value="admin"
                                checked={role === "admin"}
                                onChange={(e) => setRole(e.target.value)}
                                className="me-1" />
                            <label htmlFor="admin" className="me-3">Admin</label>

                            <input type="radio"
                                id="patient"
                                name="role"
                                value="patient"
                                checked={role === "patient"}
                                onChange={(e) => setRole(e.target.value)}
                                className="me-1" />
                            <label htmlFor="patient" className="me-3">Patient</label>

                            <input type="radio"
                                id="doctor"
                                name="role"
                                value="doctor"
                                checked={role === "doctor"}
                                onChange={(e) => setRole(e.target.value)}
                                className="me-1" />
                            <label htmlFor="doctor">Doctor</label>
                        </div>
                    </div>

                    {error && <p className="text-danger">{error}</p>}

                    <div className="col-12 mt-4">
                        <button type="submit" className="btn btn-success w-100">Login</button>
                    </div>
                    <br />
                    <p className="form-text">
                        <a href="/forgotpassword" className="form-link text-black">Forgot Password?</a>
                    </p>
                    <p className="form-text">
                        Don't have an account? <a href="/register" className="form-link text-black">Create an Account</a>
                    </p>
                </form>
            </div>
      </div>
    </div>
    </section>
  );
};

export default LoginRegister;
