import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../index.css"; 

const LoginRegister = () => {
  const [isSignUp, setIsSignUp] = useState(false);
  const navigate = useNavigate(); 

  const handleToggle = () => {
    setIsSignUp((prev) => !prev);
  };

  // Login State
  const [loginEmail, setLoginEmail] = useState('');
  const [loginPassword, setLoginPassword] = useState('');
  const [loginRole, setLoginRole] = useState('patient');
  const [error, setError] = useState('');

  const sampleUsers = [
    { email: "admin@medilocker.com", password: "admin123", role: "admin" },
    { email: "kumaran@medilocker.com", password: "kumaran123", role: "patient" },
    { email: "john@medilocker.com", password: "john123", role: "doctor" }
  ];

  const handleLogin = (e) => {
    e.preventDefault();
    const user = sampleUsers.find(user => 
      user.email === loginEmail && user.password === loginPassword && user.role === loginRole
    );

    if (user) {
      localStorage.setItem("isLoggedIn", "true");
      localStorage.setItem("userRole", user.role);
      localStorage.setItem("userEmail", user.email);
      navigate(user.role === "admin" ? "/admindashboard" : user.role === "patient" ? "/dashboard" : "/doctordashboard");
    } else {
      setError("Invalid email, password, or role selection.");
    }
  };

  // Registration State
  const [registerName, setRegisterName] = useState('');
  const [registerEmail, setRegisterEmail] = useState('');
  const [registerDob, setRegisterDob] = useState('');
  const [registerPhone, setRegisterPhone] = useState('');
  const [registerPassword, setRegisterPassword] = useState('');
  const [registerConfirmPassword, setRegisterConfirmPassword] = useState('');
  const [registerRole, setRegisterRole] = useState('patient'); 

  const handleRegister = (e) => {
    e.preventDefault();
    console.log({ registerName, registerEmail, registerDob, registerPhone, registerPassword, registerConfirmPassword, registerRole });
  };
  
  return (
    <section className="mt-5 pt-5">
      <div className={`wrapper ${isSignUp ? "animated-signup" : ""}`}>
        <div className="inner-container">
          {/* Sign Up Form */}
          <div className="registerform card sign-up">
            <div className="card-body">
              <h2 className="card-title p-3">Create an Account</h2>
              <form onSubmit={handleRegister}>
                <input type="text" value={registerName} onChange={(e) => setRegisterName(e.target.value)} placeholder="Name" required className="form-control" />
                <input type="email" value={registerEmail} onChange={(e) => setRegisterEmail(e.target.value)} placeholder="Email" required className="form-control" />
                <input type="date" value={registerDob} onChange={(e) => setRegisterDob(e.target.value)} required className="form-control" />
                <input type="tel" value={registerPhone} onChange={(e) => setRegisterPhone(e.target.value)} placeholder="Phone" required className="form-control" />
                <input type="password" value={registerPassword} onChange={(e) => setRegisterPassword(e.target.value)} placeholder="Password" required className="form-control" />
                <input type="password" value={registerConfirmPassword} onChange={(e) => setRegisterConfirmPassword(e.target.value)} placeholder="Confirm Password" required className="form-control" />
                <button type="submit" className="btn btn-primary w-100">Register</button>
              </form>
              <p className="toggle-text" onClick={handleToggle}>Already have an account? Sign In</p>
            </div>
          </div>
          
          {/* Sign In Form */}
          <div className="registerform card sign-in">
            <div className="card-body">
              <h2 className="card-title p-3">Sign In</h2>
              {error && <p className="error-text">{error}</p>}
              <form onSubmit={handleLogin}>
                <input type="email" value={loginEmail} onChange={(e) => setLoginEmail(e.target.value)} placeholder="Email" required className="form-control" />
                <input type="password" value={loginPassword} onChange={(e) => setLoginPassword(e.target.value)} placeholder="Password" required className="form-control" />
                <button type="submit" className="btn btn-primary w-100">Login</button>
              </form>
              <p className="toggle-text" onClick={handleToggle}>Don't have an account? Register</p>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default LoginRegister;
