import React, { useState } from "react";
import Header from "../components/Header";
import Footer from "../components/Footer";

const Documents = () => {
  const [activeTab, setActiveTab] = useState("Documents");
  const [searchTerm, setSearchTerm] = useState("");

  const data = {
    Documents: [
      { name: "Medical Report.pdf", link: "#" },
      { name: "Lab Test Results.docx", link: "#" },
      { name: "Health Summary.txt", link: "#" }
    ],
    Xrays: [
      { name: "Chest X-ray.png", link: "#" },
      { name: "Dental X-ray.jpg", link: "#" },
      { name: "Spine MRI.jpeg", link: "#" }
    ],
    Prescriptions: [
      { name: "Blood Pressure Medicine.pdf", link: "#" },
      { name: "Diabetes Treatment.docx", link: "#" }
    ]
  };

  const handleTabChange = (tab) => setActiveTab(tab);

  return (
    <div className="document">      
      <Header />
      <section className="document-section container mt-4">
        <h2 className="mb-3">My Document Center</h2>
        <div className="d-flex justify-content-between align-items-center mb-3">
          <div>
            {Object.keys(data).map((tab) => (
              <button
                key={tab}
                className={`btn me-2 ${activeTab === tab ? "btn-success" : "btn-outline-success"}`}
                onClick={() => handleTabChange(tab)}
              >
                {tab}
              </button>
            ))}
          </div>
          <input
            type="text"
            className="form-control w-25"
            placeholder="Search documents..."
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
        <div className="row">
          {data[activeTab]
            .filter((doc) => doc.name.toLowerCase().includes(searchTerm.toLowerCase()))
            .map((doc, index) => (
              <div key={index} className="col-md-4 mb-4">
                <div className="card border-danger text-center p-3">
                  <h5 className="card-title">{doc.name}</h5>
                  <div className="mt-3">
                    <a href={doc.link} className="btn btn-primary me-4">View</a>
                    <a href={doc.link} download className="btn btn-success">Download</a>
                  </div>
                </div>
              </div>
            ))}
        </div>
      </section>
      <Footer />
    </div>
  );
};

export default Documents;
