import React, { useState } from "react";

const ManageDoctor = () => {
  const [doctors, setDoctors] = useState([
    { id: 1, name: "Dr. John", email: "dr.john@medilocker.com", dob: "1980-05-15", phone: "987-654-3210", gender: "Male", address: "456 Clinic St, Chennai, TN", specialty: "Cardiology", experience: "15 years" },
    { id: 2, name: "Dr. Smith", email: "dr.smith@medilocker.com", dob: "1985-08-22", phone: "912-345-6789", gender: "Male", address: "789 Med Street, Bangalore, KA", specialty: "Dermatology", experience: "10 years" },
    { id: 3, name: "Dr. Alice", email: "dr.alice@medilocker.com", dob: "1975-02-10", phone: "900-111-2222", gender: "Female", address: "123 Health Ave, Mumbai, MH", specialty: "Neurology", experience: "20 years" }
  ]);

  const [formData, setFormData] = useState({
    id: null, name: "", email: "", dob: "", phone: "", gender: "", address: "", specialty: "", experience: ""
  });

  const [isEditing, setIsEditing] = useState(false);
  const [search, setSearch] = useState("");
  const [filterSpecialty, setFilterSpecialty] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const doctorsPerPage = 2;

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleAddDoctor = () => {
    if (!formData.name || !formData.email || !formData.specialty || !formData.experience) {
      alert("Please fill in all required fields.");
      return;
    }
    const newDoctor = { ...formData, id: doctors.length + 1 };
    setDoctors([...doctors, newDoctor]);
    resetForm();
  };

  const handleEditDoctor = (doctor) => {
    setFormData(doctor);
    setIsEditing(true);
  };

  const handleUpdateDoctor = () => {
    setDoctors(doctors.map(doc => (doc.id === formData.id ? formData : doc)));
    resetForm();
  };

  const handleDeleteDoctor = (id) => {
    setDoctors(doctors.filter(doc => doc.id !== id));
  };

  const resetForm = () => {
    setFormData({ id: null, name: "", email: "", dob: "", phone: "", gender: "", address: "", specialty: "", experience: "" });
    setIsEditing(false);
  };

  // Filter and Search Logic
  const filteredDoctors = doctors
    .filter((doc) => doc.name.toLowerCase().includes(search.toLowerCase()) || doc.specialty.toLowerCase().includes(search.toLowerCase()))
    .filter((doc) => (filterSpecialty ? doc.specialty === filterSpecialty : true));

  // Pagination Logic
  const indexOfLastDoctor = currentPage * doctorsPerPage;
  const indexOfFirstDoctor = indexOfLastDoctor - doctorsPerPage;
  const currentDoctors = filteredDoctors.slice(indexOfFirstDoctor, indexOfLastDoctor);

  const totalPages = Math.ceil(filteredDoctors.length / doctorsPerPage);
  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  return (
    <div className="manage-doctors">
      <section className="dashboardsection">
        <div className="container mt-4">
          <h3 className="text-center mt-3">Admin Panel</h3>
          <h2 className="text-start">Manage Doctors</h2>
          

          {/* Search & Filter */}
          <div className="d-flex justify-content-between mt-3">
            <input
              type="text"
              className="form-control me-2"
              placeholder="Search by Name or Specialty"
              value={search}
              onChange={(e) => setSearch(e.target.value)}
            />
            <select className="form-control" value={filterSpecialty} onChange={(e) => setFilterSpecialty(e.target.value)}>
              <option value="">Filter by Specialty</option>
              {[...new Set(doctors.map(doc => doc.specialty))].map((spec, index) => (
                <option key={index} value={spec}>{spec}</option>
              ))}
            </select>
          </div>

          {/* Doctor Form */}
          <div className="mt-4">
            <h4>{isEditing ? "Edit Doctor" : "Add Doctor"}</h4>
            <hr className="border border-3 border-success" />
            <div className="row g-3 mt-2">
              <div className="col-md-6">
                <div className="form-floating">
                  <input type="text" className="form-control" name="name" value={formData.name} onChange={handleChange} />
                  <label>Name</label>
                </div>
              </div>
              <div className="col-md-6">
                <div className="form-floating">
                  <input type="date" className="form-control" name="dob" value={formData.dob} onChange={handleChange} />
                  <label>Date of Birth</label>
                </div>
              </div>
              <div className="col-md-6">
                <div className="form-floating">
                  <input type="email" className="form-control" name="email" value={formData.email} onChange={handleChange} />
                  <label>Email</label>
                </div>
              </div>
              <div className="col-md-6">
                <div className="form-floating">
                  <input type="text" className="form-control" name="phone" value={formData.phone} onChange={handleChange} />
                  <label>Phone</label>
                </div>
              </div>
              <div className="col-md-6">
                <div className="form-floating">
                  <input type="text" className="form-control" name="specialty" value={formData.specialty} onChange={handleChange} />
                  <label>Specialty</label>
                </div>
              </div>
            </div>

            <div className="mt-3">
              {isEditing ? (
                <>
                  <button className="btn btn-success me-2" onClick={handleUpdateDoctor}>Update</button>
                  <button className="btn btn-danger" onClick={resetForm}>Cancel</button>
                </>
              ) : (
                <button className="btn btn-success" onClick={handleAddDoctor}>Add Doctor</button>
              )}
            </div>
          </div>

          {/* Doctor List */}
          <div className="mt-5">
            <h4>Doctors List</h4>
            <hr className="border border-3 border-primary" />
            {currentDoctors.length === 0 ? (
              <p className="text-muted">No doctors found.</p>
            ) : (
              <ul className="list-group">
                {currentDoctors.map((doctor) => (
                  <li key={doctor.id} className="list-group-item d-flex justify-content-between align-items-center">
                    <div>
                      ðŸ©º {doctor.name} - {doctor.specialty} - ðŸ“ž {doctor.phone}
                    </div>
                    <div>
                      <button className="btn btn-warning btn-sm me-2" onClick={() => handleEditDoctor(doctor)}>Edit</button>
                      <button className="btn btn-danger btn-sm" onClick={() => handleDeleteDoctor(doctor.id)}>Delete</button>
                    </div>
                  </li>
                ))}
              </ul>
            )}
          </div>

          {/* Pagination */}
          <nav className="mt-3">
            <ul className="pagination">
              {[...Array(totalPages)].map((_, index) => (
                <li key={index} className={`page-item ${currentPage === index + 1 ? "active" : ""}`}>
                  <button className="page-link" onClick={() => paginate(index + 1)}>{index + 1}</button>
                </li>
              ))}
            </ul>
          </nav>
        </div>
      </section>
    </div>
  );
};

export default ManageDoctor;
